resources.string:
if test ! -e ~/.ssh;
then mkdir ~/.ssh;
	chmod 700 ~/.ssh;
fi;
if test ! -e ~/.ssh/authorized_keys;
then touch ~/.ssh/authorized_keys;
	chmod 600 ~/.ssh/authorized_keys;
fi;
cp ~/.ssh/authorized_keys /tmp/authorized_keys.bak;
grep -v 'Multikey by Termius' /tmp/authorized_keys.bak > ~/.ssh/authorized_keys;
echo '$1' >> ~/.ssh/authorized_keys

if test ! -e $1;
then mkdir $1;
	chmod 700 $1;
fi;
if test ! -e "$1/$2";
then touch "$1/$2";
	chmod 600 "$1/$2";
fi;
echo '$3' >> "$1/$2";
